import { connect } from 'react-redux';

import App from '../App';

const mapStateToProps = (state) => ({
  message: state.message,
});

const Game = connect(mapStateToProps)(App);

export default Game;

//this container will use connect utility from react-redux to pass the state.message to the message props of App component
