import React from 'react';
import PropTypes from 'prop-types';
import Sky from './Sky';
import Ground from './Ground';
import CannonBase from './CannonBase';
import CannonPipe from './CannonPipe';
import CurrentScore from './CurrentScore';
import NetBase from './NetBase';
import NetBackboard from './NetBackboard';

const Canvas = (props) => {
  const viewBox = [
    window.innerWidth / -2,
    100 - window.innerHeight,
    window.innerWidth,
    window.innerHeight,
  ]; // sets the canvas to take up the entire screen

  return (
    <svg
      id='trash-shooter-canvas'
      preserveAspectRatio='xMaxYMax none'
      onMouseMove={props.trackMouse}
      viewBox={viewBox}>
      <defs>
        <filter id='shadow'>
          <feDropShadow dx='1' dy='1' stdDeviation='2' />
        </filter>
      </defs>
      <Sky />
      <Ground />
      <CannonPipe rotation={props.angle} />
      <CannonBase />
      <CurrentScore score={15} />
      <NetBase
        position={{ x: -500, y: -450 }}
        style={{ fill: '#30abef', stroke: '#1a1a1a', strokeWidth: '5' }}
      />
      <NetBackboard
        position={{ x: -600, y: -550 }}
        style={{ fill: '#1a1a1a', stroke: 'white', strokeWidth: '5' }}
      />
    </svg>
  );
};

Canvas.propTypes = {
  angle: PropTypes.number.isRequired,
  trackMouse: PropTypes.func.isRequired,
};

export default Canvas;
