import { connect } from 'react-redux';
import App from '../App';
import { moveObjects, startGame } from '../actions/index';

const mapStateToProps = (state) => ({
  angle: state.angle,
  gameState: state.gameState,
});

const mapDispatchToProps = (dispatch) => ({
  moveObjects: (mousePosition) => {
    dispatch(moveObjects(mousePosition));
  },
  startGame: () => {
    dispatch(startGame());
  },
});

const Game = connect(mapStateToProps, mapDispatchToProps)(App);
export default Game;

//this container will use connect utility from react-redux to pass the state.message to the message props of App component
